
//google maps api

jQuery(document).ready(function(){

var map;
function initialize() {
  var mapOptions = {
    zoom: 8,
    center: new google.maps.LatLng(-18.8785998, -41.9348767)
  };
  map = new google.maps.Map(document.getElementById('mapagv'),
      mapOptions);
}

google.maps.event.addDomListener(window, 'load', initialize);

});